drop database if exists hospital;
create database hospital default character set utf8;

grant all on hospital.* to 'hospital'@'%' identified by '123456';
grant all on hospital.* to 'hospital'@'localhost' identified by '123456';
flush privileges;

set names utf8;

use hospital;
drop table if exists `user`;
create table user(
	user_id varchar(10) not null comment '用户id（工号）',
	user_pwd varchar(32) not null comment '密码（MD5加密）',
	user_type tinyint not null comment '用户类型（0管理员，1医生，2护士）',/*用户类型（0管理员，1医生，2护士）*/
	person_id int(11) not null comment '关联个人id',/*关联到具体用户的id*/
	primary key (user_id)
) engine=InnoDB default charset=utf8;


/*-----患者表-----*/
drop table if exists `patient`;
create table patient(
	pat_id int(11) not null comment '住院号',
	pat_name varchar(20) not null comment '患者姓名',
	cash_pledge int(10) not null default 0 comment '押金余额',
	admission_date int(13) not null comment '入院日期',
	record_data int(13) comment '记录日期',
	department varchar(20) comment '科别',
	sickbad varchar(20) comment '病床',
	medical_history text comment '住院病历',
	medical_record text comment '病程记录',
	sex varchar(6) comment '患者性别',
	age tinyint(3) comment '患者年龄',
	marriage tinyint(1) comment '婚姻情况（0：未婚 1：已婚）',
	birthplace varchar(50) comment '出生地',
	nationality varchar(10) comment '民族',
	job varchar(50) comment '职业',
	company varchar(50) comment '工作单位',
	address varchar(50) comment '住址',
	provider varchar(50) comment '供史者',
	reliability tinyint(1) comment '可靠度（0-9）',
	phone varchar(12) comment '患者手机号',
	room_id int(11) comment '房间id',
	primary key (pat_id)
) engine=InnoDB default charset=utf8;



/*-----医生表-----*/
drop table if exists `doctor`;
create table doctor(
	doc_id int(11) not null auto_increment comment '医生id',
	doc_name varchar(20) not null comment '医生姓名',
	doc_level tinyint(3) not null comment '医生等级（1住院医师，2主治医师，3副主任医师，4主任医师，5配药）',/*（1住院医师，2主治医师，3副主任医师，4主任医师）*/
	is_special tinyint(3) not null comment '是否是专家（1是，0不是）',
	doc_sex varchar(6) not null comment '医生性别',
	doc_phone varchar(12) not null comment '医生手机号',
	entry_date int(13) not null comment '入职日期',
	room_id int(11) not null comment '房间id',
	primary key (doc_id)
) engine=InnoDB default charset=utf8;




/*-----护士表-----*/
drop table if exists `nurse`;
create table nurse(
	nur_id int(11) not null auto_increment comment '护士id',
	nur_name varchar(20) not null comment '护士姓名',
	nur_level tinyint(3) not null comment '护士等级（1普通护士，2护士长）',/*（1普通护士，2护士长）*/
	nur_sex varchar(6) not null comment '护士性别',
	nur_phone varchar(12) not null comment '护士手机号',
	entry_date int(13) not null comment '入职日期',
	room_id int(11) not null comment '房间id',
	primary key (nur_id)
) engine=InnoDB default charset=utf8;

/*-----费用表-----*/
drop table if exists `fee`;
create table fee(
	fee_id int(11) not null auto_increment comment '费用id',
	figure int(11) not null comment '费用金额',
	fee_desc varchar(255) not null comment '费用说明',
	expend_time int(13) not null comment '支出时间',
	usefor tinyint(3) not null comment '用途（1药品 2手术 3住院 4其他）',/*（1药品 2手术 3住院 4其他）*/
	pat_id int(11) not null comment '患者id',
	primary key (fee_id)
) engine=InnoDB default charset=utf8;


/*-----患者医生表-----*/
drop table if exists `pat_doc`;
create table pat_doc(
	pat_id int(11) not null comment '患者id',
	doc_id int(11) not null comment '医生id',
	primary key (pat_id, doc_id)
) engine=InnoDB default charset=utf8;

/*-----房间表-----*/
drop table if exists `room`;
create table room(
	room_id int(11) not null auto_increment comment '房间id',
	room_name varchar(11) not null comment '房间号',
	headcount tinyint(3) comment '总人数',
	rt_id int(11) not null comment '房间类型id',
	primary key(room_id)
) engine=InnoDB default charset=utf8;


/*-----房间类型表-----*/
drop table if exists `room_type`;
create table room_type(
	rt_id int(11) not null auto_increment comment '房间类型id',
	rt_name varchar(11) not null comment '房间类型名称',
	primary key(rt_id)
) engine=InnoDB default charset=utf8;

/*-----设施表-----*/
drop table if exists `facility`;
create table facility(
	fac_id int(11) not null auto_increment comment '设施id',
	fac_name varchar(20) not null comment '设施名称',
	fac_state varchar(20) not null comment '设施状态',
	primary key(fac_id)
) engine=InnoDB default charset=utf8;


/*-----临时医嘱表-----*/
drop table if exists `temporary_orders`;
create table temporary_orders(
	to_id int(11) not null auto_increment comment '临时医嘱id',
	to_content varchar(255) not null comment '临时医嘱内容',
	create_time int(13) not null comment '创建时间',
	execute_time int(13) default '0' comment  '执行时间',
	doctor_name varchar(20) not null comment '医生姓名',
	doctor_sign varchar(255) not null default '' comment '医生签字图片路径',
	nurse_name varchar(20) default '' comment '护士姓名',
	nurse_sign varchar(255) default '' comment '护士签字图片路径',
	pat_id int(11) not null comment '所属患者id',
	primary key(to_id)
) engine=InnoDB default charset=utf8;







/*-----长期医嘱表-----*/
drop table if exists `standing_orders`;
create table standing_orders(
	so_id int(11) not null auto_increment comment '长期医嘱id',
	so_content varchar(255) not null comment '长期医嘱内容',
	start_create_time int(13) not null comment '开始创建时间',
	start_time int(13) default 0 comment '开始执行时间',
	stop_create_time int(13) default 0 comment '停止创建时间',
	stop_time int(13) default 0 comment '停止执行时间',
	start_doctor_name varchar(20) default '' comment '开始医生',
	start_doctor_sign varchar(255) default '' comment '医生签字图片路径',
	start_nurse_name varchar(20) default '' comment '开始护士',
	start_nurse_sign varchar(255) default '' comment '医生签字图片路径',
	stop_doctor_name varchar(20) default '' comment '停止医生',
	stop_doctor_sign varchar(255) default '' comment '医生签字图片路径',
	stop_nurse_name varchar(20) default '' comment '停止护士',
	stop_nurse_sign varchar(255) default '' comment '医生签字图片路径',
	pat_id int(11) not null comment '所属患者id',
	primary key(so_id)
) engine=InnoDB default charset=utf8;








